// Navbar.js
import React, { useEffect } from 'react';
import "../css/Navbar.css";
import { Link } from 'react-router-dom';
import { useAuth } from './AuthContext';

const Navbar = () => {

  const { token, logout } = useAuth(); //get the token from the authentication context

  useEffect(()=>{
    
    console.log("navbar: ", token)
  },[token])

  return (
    <div>
      <nav className='navbar'>
        <div>
          <div>
            <Link to="/">Home</Link>
          </div>
          {!token && (
            <div className='sign-up-link'>
              <Link to="/signup">Sign Up</Link> //only show if user is not logged in.
            </div>
            )}
          {!token ? (
            <div>
              <Link to="/login">Login</Link> {/*only show if user is not logged in*/}
            </div>
          ) : (<div>
                <button onClick={logout}>Logout</button> {/*only show if user is logged in*/}
              </div>)}
        </div>

      </nav>
    </div>
  );
};

export default Navbar;
