import React, { useEffect, useState } from "react";

function GetProducts() {
    const [ products, setProducts ] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(()=>{
        fetch('https://fakestoreapi.com/products')
            .then(response=>response.json())
            .then(data =>
                setProducts(data))
            .catch(error => console.error('Error fetching products', error));
    },[])
    
    const filteredProducts = products.filter(product =>
        product.title.toLowerCase().includes(searchQuery.toLowerCase())
        );

    const addToCart = (productId) => {
        const cartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
        const productToAdd = products.find(product => product.id === productId);
        if(productToAdd) {
            cartItems.push(productToAdd);
            localStorage.setItem('cartItems', JSON.stringify(cartItems));
        }

        //make a POST request to teh api to add the product to the cart
        fetch('https://fakestoreapi.com/carts', {
            method: "POST",
            headers: {
                'Content-Type' : 'application/json'
            },
            body: JSON.stringify({
                date: new Date().toISOString(),
                products: [{ productId: productId, quantity: 1 }]
            })
        })
        .then(res => res.json())
        .then(data => {
            console.log('Product added to cart: ',data);
        })
        .catch(error => {
            console.error('Error adding product to cart:', error);
        });
     }
    


    return (
        <div>
            <input
            type="text"
            placeholder="search products..."
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
            />
           <h3>Products</h3>
           <ul>
            {filteredProducts.map(product => (
                <li key={product.id} style={{marginTop:"2%"}}>
                    <div style={{fontSize:"30px"}}><strong>{product.title}</strong></div>
                    <img src={product.image} alt="product" style={{width:"150px", height:"150px"}}/>
                    <div>Price: ${product.price}</div>
                    <div>Category: {product.category}</div>
                    <div>Rating: {product.rating.rate}/5</div>
                    <div>Inventory: {product.rating.count}</div>
                    <button onClick={() => addToCart(product.id)}>Add to Cart</button>
                </li>
            ))}
            </ul> 
        </div>
    )
}

export default GetProducts;
